do identifierArray.%Push(PASLocalContent) } */
// National Code if NationalCode '= "" { set NationalCodeContent = ##class(%DynamicObject).%New() Set NationalCodeAssigner = ##class(%DynamicObject).%New() set NationalCodeContent.system = "https://fhir.hl7.org.uk/Id/gmc-number" set NationalCodeContent.value = NationalCode do identifierArray.%Push(NationalCodeContent) }
if ADLocal '= "" { set ADLocalContent = ##class(%DynamicObject).%New() Set ADLocalAssigner = ##class(%DynamicObject).%New() set ADLocalContent.system = "https://www.northerncarealliance.nhs.uk/PAS-LOCAL" set ADLocalContent.value = ADLocal //need to be looked up?? set ADLocalAssigner.identifier = "RW6orRM3" //need to be looked up?? set ADLocalContent.assigner = ADLocalAssigner
