// GP Name set GPnameArray = ##class(%DynamicArray).%New() set GPnameContent = ##class(%DynamicObject).%New() Set GPnameAssigner = ##class(%DynamicObject).%New() set GPnameContent.use = "official" set GPnameContent.family = GPFamilyName set GPnameContent.given = GPGivenName set GPnameContent.prefix = GPPrefix do GPnameArray.%Push(GPnameContent)
