	set tsc = $$$OK

	Try {
		if Type = "TRUST" {set OdsCode = pArr.GetAt("LocalCodeAssigner")}
		elseif Type = "GP" {set OdsCode = pArr.GetAt("odsOrganisationCode")}

		;$$$TRACE("OdsCode: "_OdsCode)

		// Create a new resource
		set resource = ##class(%DynamicObject).%New()

		// Create a "Resource Type" and add it to the resource
		set resource.resourceType = "Organization"

		// PAS Local
		set identifierArray = ##class(%DynamicArray).%New()
		if OdsCode '= ""
		{
			set OdsCodeContent = ##class(%DynamicObject).%New()
			Set OdsCodeIdentifierContent = ##class(%DynamicObject).%New()
			set OdsCodeContent.system = "https://www.northerncarealliance.nhs.uk/ODS-LOCAL"
			set OdsCodeContent.use = "official"
			set OdsCodeContent.value = OdsCode

			do identifierArray.%Push(OdsCodeContent)
		}
		else {
			Set mygenex = ##class(%Exception.General).%New("Missing ODS Code","5001",,
    	                "The inbound HL7 message doesn't contain an ODS CODE")
    		Throw mygenex
		}

		set resource.identifier = identifierArray

		// Organization name
		if $LISTBUILD("RW6", "RM3") [ OdsCode {Set resource.name = "NORTH MANCHESTER GENERAL HOSPITAL"}
		else {Set resource.name = "GP ORGANIZATION NAME"}

    	// telecom: Hardcode address & phone
    	set telecomArray = ##class(%DynamicArray).%New()
		set phoneContent = ##class(%DynamicObject).%New()
    	set phoneContent.system = "phone"
		if $LISTBUILD("RW6", "RM3") [ OdsCode {set phoneContent.value = "01617897373"}
		else {Set phoneContent.value = "GP phone 01234567898"}
		set phoneContent.use = "work"
		do telecomArray.%Push(phoneContent)

    	set resource.telecom = telecomArray

    	// Organisation Address
		set addressArray = ##class(%DynamicArray).%New()
		set addressContent = ##class(%DynamicObject).%New()
		set addressLinesArray = ##class(%DynamicArray).%New()

		if $LISTBUILD("RW6", "RM3") [ OdsCode
		{
			do addressLinesArray.%Push("Mayo Building")
			do addressLinesArray.%Push("Salford Royal")
			do addressLinesArray.%Push("Stott Lane")
			do addressLinesArray.%Push("Salford")
		}
		else
		{
			do addressLinesArray.%Push("GP ADSRESS1")
			do addressLinesArray.%Push("GP ADSRESS2")
			do addressLinesArray.%Push("GP ADSRESS3")
			do addressLinesArray.%Push("GP ADSRESS4")
		}


		set addressContent.line = addressLinesArray
		if $LISTBUILD("RW6", "RM3") [ OdsCode {set addressContent.postalCode = "M6 8HD"}
		else {set addressContent.postalCode = "GP PostCode"}

		do addressArray.%Push(addressContent)

		set resource.address = addressArray

		;$$$TRACE("Organization= "_resource.%ToJSON())

		set pOrganizationResource = resource
	}
	Catch ex {
		Set tsc=ex.AsStatus()
		do ex.Log()
		;$$$TRACE("In the CATCH block")
    	;$$$TRACE(ex.Name)
    	;$$$TRACE(ex.Code)
    	;$$$TRACE(ex.Data)
    	;$$$TRACE("End of the CATCH block")
	}

	Quit tsc

	set tsc = $$$OK
	Try {
		// Create a new resource
		set resource = ##class(%DynamicObject).%New()

		// Create a "Resource Type" and add it to the resource
		set resource.resourceType = "Location"

		// PAS Local
		set identifierArray = ##class(%DynamicArray).%New()

		set LocationCodeContent1 = ##class(%DynamicObject).%New()
		set LocationCodeContent1.system = "https://fhir.nhs.uk/Id/ods-site-code"
    	set LocationCodeContent1.value = pArr.GetAt("LocationCode")
		do identifierArray.%Push(LocationCodeContent1)

		set LocationCodeContent2 = ##class(%DynamicObject).%New()
		set LocationCodeContent2.system = "https://fhir.nhs.uk/Id/ods-site-code"
		set LocationCodeContent2.value = "########"
		if pArr.GetAt("LocalCodeAssigner") '= ""
		{
			set LocationCodeContent2.assigner = ..AssignerBuilder("Organization/"_pArr.GetAt("LocalCodeAssigner"))
		}

		do identifierArray.%Push(LocationCodeContent2)

		set resource.identifier = identifierArray

		// PhysicalType
		set tsc = ..CodeableConcept("https://terminology.hl7.org/4.0.0/CodeSystem-location-physical-type.html", pArr.GetAt("LocationType"), "", .physicalType, .specialtyCoding)
		set resource.physicalType = physicalType

		// Name
    	set resource.name = pArr.GetAt("LocationName")

		;$$$TRACE("Location= "_resource.%ToJSON())

		set pLocationResource = resource
	}
	Catch ex {
		Set tsc=ex.AsStatus()
		do ex.Log()
	}
	Quit tsc

set tsc = $$$OK Try { set resource = ##class(%DynamicObject).%New() set resource.resourceType = "Encounter"
// identifier set identifier = ##class(%DynamicObject).%New() set identifierArray = ##class(%DynamicArray).%New() set identifierCoding = ##class(%DynamicObject).%New() ;set identifierCoding.system = "http://example.com" // Not in the guide? to remove? set identifierCoding.value = pArr.GetAt("EncounterIdentifier") if pArr.GetAt("LocalCodeAssigner") '= "" { set identifierCoding.assigner = ..AssignerBuilder("Organization/"_pArr.GetAt("LocalCodeAssigner")) }
do identifierArray.%Push(identifierCoding) set resource.identifier = identifierArray
//status set resource.status = "finished"
// Status History set stHist = ##class(%DynamicArray).%New() set stHistperiod = ##class(%DynamicObject).%New()
set stHistperiod.status = "cancelled"
set tsc = ..SetPeriod("2022-11-01", "2022-11-10", .period) set stHistperiod.period = period
do stHist.%Push(stHistperiod) set resource.statusHistory = stHist
// Class set tsc = ..CodeableConcept("https://simplifier.net/packages/hl7.fhir.r4.core/4.0.1/files/80998/~overview", "AMB", "ambulatory", .pCodeConcept, .class) set resource.class = class
// ServiceType set tsc = ..CodeableConcept("http://snomed.info/sct", "736622005", "Aboriginal health service", .ServiceType, .Dynobject) set resource.serviceType = ServiceType
// Priority set tsc = ..CodeableConcept("http://terminology.hl7.org/CodeSystem/v3-ActPriority", "A", "ASAP", .Priority, .Dynobject) set resource.priority = Priority
// Subject set nhsNumber = pArr.GetAt("NhsNumberContent.value") Set NhsNoVerif = pArr.GetAt("VerifStatus") set LocalCode = pArr.GetAt("LocalCode")
set Subject = ##class(%DynamicObject).%New() if (nhsNumber '= "") && (NhsNoVerif = "NSTS01") { set Subject.reference = "https://www.northerncarealliance.nhs.uk/fhir-profiles/Patient/"_pArr.GetAt("NhsNumberContent.value") } elseif LocalCode '= "" { set Subject.reference = "https://www.northerncarealliance.nhs.uk/fhir-profiles/Patient/"_pArr.GetAt("LocalCode") } set resource.subject = Subject
// EpisodeOfCare set EpisodeOfCare = ##class(%DynamicObject).%New() set EpisodeOfCare.reference = "episodeOfCare/"_pArr.GetAt("EpisodeOfCareValue") set resource.episodeOfCare = EpisodeOfCare
// BasedOn set BasedOn = ##class(%DynamicObject).%New() set BasedOn.reference = "serviceRequest/0123456" set resource.basedOn = BasedOn
// Participant set participantArray = ##class(%DynamicArray).%New() set participantTypeArray = ##class(%DynamicArray).%New() set participantType = ##class(%DynamicObject).%New()
set tsc = ..CodeableConcept("http://terminology.hl7.org/CodeSystem/v3-ParticipationType", "ATND", "attender", .partType, .Dynobject) do participantTypeArray.%Push(partType) set participantType.type = participantTypeArray do participantArray.%Push(participantType)
set resource.participant = participantArray
// Period set tsc = ..SetPeriod("2022-11-01", "2022-11-10", .period) set resource.period = period
// Location set Location = ##class(%DynamicObject).%New() set LocationObject = ##class(%DynamicObject).%New() set LocationArray = ##class(%DynamicArray).%New() set Location.reference = "Location/"_pArr.GetAt("LocationCode") set LocationObject.location = Location do LocationArray.%Push(LocationObject) set resource.location = LocationArray
;$$$TRACE("EncounterResource= "_resource.%ToJSON())
set pEncounterResource = resource } Catch ex { Set tsc=ex.AsStatus() do ex.Log() } quit tsc

	set tsc = $$$OK
	Try {
		set resource = ##class(%DynamicObject).%New()
		set resource.resourceType = "ServiceRequest"

		// identifier
		set identifier = ##class(%DynamicObject).%New()
		set identifierArray = ##class(%DynamicArray).%New()
		set identifierCoding = ##class(%DynamicObject).%New()
		set identifierCoding.value = "########"
		if pArr.GetAt("NhsNumberAssigner") '= ""
		{
			set identifierCoding.assigner = ..AssignerBuilder("Organization/"_pArr.GetAt("NhsNumberAssigner"))
		}
		do identifierArray.%Push(identifierCoding)
		set resource.identifier = identifierArray

		//status
		set resource.status = pArr.GetAt("SRstatus")

		// Priority
		set tsc = ..CodeableConcept("http://terminology.hl7.org/CodeSystem/v3-ActPriority", "A", "ASAP", .Priority, .Dynobject)
		set resource.priority = Priority

		//Category
		set category = ##class(%DynamicArray).%New()
		set category.text = pArr.GetAt("SRcategoryText")
		set resource.category = category

		// Subject
		set Subject = ##class(%DynamicObject).%New()
    	if pArr.GetAt("NhsNumberContent.value") '= ""
    	{
    	    set Subject.reference = "Patient/"_pArr.GetAt("NhsNumberContent.value")
    	}
		elseif pArr.GetAt("LocalCode") '= ""
    	{
    	    set Subject.reference = "Patient/"_pArr.GetAt("LocalCode")
    	}
		set resource.subject = Subject

		// Encounter
		set Encounter = ##class(%DynamicObject).%New()
		set Encounter.reference = "Encounter/0123456"
		set resource.encounter = Encounter

		// LocationReference
		set LocationReference = ##class(%DynamicObject).%New()
		set LocationReference.reference = "Location/"_pArr.GetAt("LocationCode")
		set resource.locationReference = LocationReference

		;$$$TRACE("ServiceRequestResource= "_resource.%ToJSON())

		set pServiceRequestResource = resource
	}
	Catch ex {
		Set tSC=ex.AsStatus()
		do ex.Log()
	}
	quit tsc
